package com.example.ppm_proyecto.presentation.util

import android.content.ClipData
import android.content.ClipboardManager
import android.content.Context
import java.text.SimpleDateFormat
import java.util.*

private const val DEFAULT_DATE_PATTERN = "dd/MM/yyyy HH:mm"

fun formatDate(date: Date?, pattern: String = DEFAULT_DATE_PATTERN, nullText: String = "Desconocida"): String {
    if (date == null) return nullText
    return try {
        val formatter = SimpleDateFormat(pattern, Locale.getDefault())
        formatter.format(date)
    } catch (_: Throwable) { nullText }
}

fun copyToClipboard(context: Context, text: String, label: String = "Copied Text") {
    val clipboard = context.getSystemService(Context.CLIPBOARD_SERVICE) as ClipboardManager
    clipboard.setPrimaryClip(ClipData.newPlainText(label, text))
}
