package com.example.ppm_proyecto.presentation.ui.components

import androidx.compose.foundation.layout.Arrangement
import androidx.compose.foundation.layout.Column
import androidx.compose.foundation.layout.fillMaxSize
import androidx.compose.foundation.layout.fillMaxWidth
import androidx.compose.foundation.layout.padding
import androidx.compose.material3.Button
import androidx.compose.material3.OutlinedButton
import androidx.compose.material3.Text
import androidx.compose.runtime.Composable
import androidx.compose.ui.Alignment
import androidx.compose.ui.Modifier
import androidx.compose.ui.unit.dp

@Composable
fun BasicLoginScreen(
    onLoginStudent: () -> Unit,
    onLoginTeacher: () -> Unit,
    onCreateAccount: () -> Unit
) {
    Column(
        modifier = Modifier.fillMaxSize().padding(24.dp),
        verticalArrangement = Arrangement.Center,
        horizontalAlignment = Alignment.CenterHorizontally
    ) {
        Text("Login")
        Button(onClick = onLoginStudent, modifier = Modifier.fillMaxWidth().padding(top = 16.dp)) {
            Text("Entrar como Estudiante")
        }
        Button(onClick = onLoginTeacher, modifier = Modifier.fillMaxWidth().padding(top = 8.dp)) {
            Text("Entrar como Maestro")
        }
        OutlinedButton(onClick = onCreateAccount, modifier = Modifier.fillMaxWidth().padding(top = 24.dp)) {
            Text("Crear cuenta")
        }
    }
}

@Composable
fun BasicRegisterScreen(
    onRegistered: () -> Unit,
    onBack: () -> Unit
) {
    Column(
        modifier = Modifier.fillMaxSize().padding(24.dp),
        verticalArrangement = Arrangement.Center,
        horizontalAlignment = Alignment.CenterHorizontally
    ) {
        Text("Registro")
        Button(onClick = onRegistered, modifier = Modifier.fillMaxWidth().padding(top = 16.dp)) {
            Text("Registrar y volver al Login")
        }
        OutlinedButton(onClick = onBack, modifier = Modifier.fillMaxWidth().padding(top = 8.dp)) {
            Text("Volver")
        }
    }
}

@Composable
fun BasicTeacherHomeScreen(
    onOpenCourse: (String) -> Unit,
    onOpenProfile: () -> Unit
) {
    Column(
        modifier = Modifier.fillMaxSize().padding(24.dp),
        verticalArrangement = Arrangement.spacedBy(12.dp),
        horizontalAlignment = Alignment.CenterHorizontally
    ) {
        Text("Inicio Maestro")
        Button(onClick = { onOpenCourse("COURSE_1") }) { Text("Abrir curso COURSE_1") }
        Button(onClick = { onOpenProfile() }) { Text("Ir a Perfil") }
    }
}

@Composable
fun BasicCourseDetailsScreen(courseId: String, onBack: () -> Unit) {
    Column(
        modifier = Modifier.fillMaxSize().padding(24.dp),
        verticalArrangement = Arrangement.spacedBy(12.dp),
        horizontalAlignment = Alignment.CenterHorizontally
    ) {
        Text("Detalles del curso: $courseId")
        OutlinedButton(onClick = onBack) { Text("Volver") }
    }
}

@Composable
fun BasicProfileScreen(
    onBack: () -> Unit,
    onOpenSecurity: () -> Unit,
    onOpenAppearance: () -> Unit
) {
    Column(
        modifier = Modifier.fillMaxSize().padding(24.dp),
        verticalArrangement = Arrangement.spacedBy(12.dp),
        horizontalAlignment = Alignment.CenterHorizontally
    ) {
        Text("Perfil")
        Button(onClick = onOpenSecurity) { Text("Seguridad") }
        Button(onClick = onOpenAppearance) { Text("Apariencia") }
        OutlinedButton(onClick = onBack) { Text("Volver") }
    }
}

